router.get('/user/:id', user.getUserInfo);
// 获取订单信息
router.get('/user/:id/orders', user.orders);
// 下单
router.put('/user/:mail/orders', user.placeOrder);
// 获取验证码
router.get('/verifyCode', user.verifyCode);

export default router;

