import crypto from 'crypto'
import redisClient from '../../dbs/redis.js'
import Email from '../../utils/smtp'
import userModel from '../../models/user/user.js'
import shop from '../../models/shop/shop.js'
import goods from '../../models/shop/goods.js'
let nodeMailer = require('nodemailer');
class User {
  constructor () {
    this.mailLogin = this.mailLogin.bind(this);
    this.usernameLogin = this.usernameLogin.bind(this);
    this.loginOut = this.loginOut.bind(this);
    this.register = this.register.bind(this);
    this.changePassword = this.changePassword.bind(this);
    this.changePicture = this.changePicture.bind(this);
    this.getUserInfo = this.getUserInfo.bind(this);
    this.orders = this.orders.bind(this);
    this.placeOrder = this.placeOrder.bind(this);
    this.verifyCode = this.verifyCode.bind(this);
    this.Md5 = this.Md5.bind(this);
  }
  // 退出登录
  loginOut (req, res, next) {
    delete req.session.userInfo
    res.status(200).send({
      msg: '已退出登录！',
      data: {}
    })
    // res.status(400).send('Bad Request');
  }
  // 判断是否登录
  isLogin (req, res, next) {
    console.log('---判断登录状态---')
    console.log(req.session)
    console.log('---判断登录状态---')
    if (!req.session.userInfo) {
      // res.redirect(301, 'http://localhost:8080/#/login');
      res.status(401).send({
        msg: '未登录!',
        data: {}
      })
    } else {
      res.status(200).send({
        status: 200,
        msg: '已登录!',
        data: req.session.userInfo
      })
    }
  }
  // 账号密码登录
  async usernameLogin (req, res, next) {
    const {username, password} = req.body;
    if (username.indexOf(' ') !== -1 || password.indexOf(' ') !== -1) {
      res.status(401).send({
        status: 401,
        msg: '账号密码不能为空！',
        data: {}
      })
      return false;
    }
    const result = await userModel.findOne({username, password});
    if (!result) {
      res.status(401).send({
        status: 401,
        msg: '账号或密码错误',
        data: {}
      })
    } else {
      req.session.userInfo = result
      console.log('---账号密码登录---')
      console.log(req.session)
      console.log('---账号密码登录---')
      res.status(200).send({
        status: 200,
        msg: '登录成功',
        data: {
          name: result.name,
          username: result.username,
          shopId: result.shopId,
          headPortrait: result.headPortrait,
          mail: result.mail,
          merchant: result.merchant
        }
      })
    }
  }
  // 邮箱登录
  mailLogin (req, res, next) {
    const { mail, code } = req.body;
    if (!mail || !code ) {
      res.status(401).send({
        msg: '请输入正确的邮箱'
      })
      return false;
    }
    
    redisClient.get(`mail:${mail}`, async (err, result) => {
      if ( err || !result ) {
        res.status(503).send({
          msg: '请重试！',
          data: {}
        })
        return false;
      }
      if (code.toLowerCase() != result.toLowerCase() ) {
        res.status(400).send({
          msg: '请输入正确的验证码',
          data: {}
        })
        return false;
      } else {
        let userInfo = await userModel.findOne({mail})
        redisClient.del(`mail:${mail}`)
        if ( userInfo == null ) {
          req.session.register = mail;
          // res.redirect(`register/${mail}`);
          res.status(200).send({
            msg: '账号未注册！',
            data: null
          })
        } else {
          console.log('--邮箱登录--')
          req.session.userInfo = userInfo
          console.log(req.session)
          console.log('--邮箱登录--')
          res.status(200).send({
            msg: '登陆成功！',
            data: {
              name: userInfo.name,
              username: result.username,
              shopId: result.shopId,
              headPortrait: userInfo.headPortrait,
              mail: userInfo.mail
            }
          })
        }
      }
    })
    
  }
  // 用户注册
  register (req, res, next) {
    const { username, password } = req.body;
    const { mail } = req.params;
    if ( !req.session.register || req.session.register !== mail ) {
      res.status(401).send({
        msg: '无效访问',
        data: {}
      })
      return false;
    }
    if ( !username || !password || !mail ) {
      res.status(400).send({
        msg: '账号、密码为空，请重试！',
        data: {}
      })
      return false
    }
    let create = new userModel ({
      mail: mail,
      username: username,
      password: password,
      name: '爱泡温泉的小雪人',
      orders: [],
    })
    create.save( async (err, result ) => {
      if ( err ) {
        console.log( err );
        res.status(500).send({
          msg: '服务器繁忙，请稍后再试！',
